INSERT INTO `` (`id`,`Title`,`Company_Name`,`Place`,`Deadline`,`Experience`,`Level`,`Salary`,`Education`,`Description`,`Requirement`,`Number_Employee`,`Source_Picture`) VALUES (15,'Tuyển NV Phục Vụ Chế Biến Suất Ăn Hàng Không','CÔNG TY TNHH DỊCH VỤ VIỆT TRUNG SƠN','80/40 Huỳnh Văn Nghệ, Phường 15, Quận Tân Bình','23-01-2024','Không yêu cầu','Nhân Viên','6 - 8.5 triệu','Đang cập nhật','Quản lý cấp phát vật tư - Phân chia, đóng gói thực phẩm hàng không - Các công việc - Sắp xếp nguyên vật liệu phục vụ sơ chế thực phẩm - Phục vụ công việc sơ chế thực phẩm hàng không - Vệ sinh dụng cụ thực phẩm Yêu cầu công việc - Độ tuổi từ 18 đến 45 tuổi (sức khỏe tốt) - Lý lịch tốt - Chịu khó, cẩn thận Quyền lợi: - Thời gian làm việc 8 tiếng / ca, xoay ca : (sáng : 6h- 14h,8h- 16h, 14h- 22h), được nghỉ 1 ngày / tuần - Làm việc lâu dài, ký hợp đồng, chế độ BHXH,BHYT,BHTN, 12 ngày phép / năm- Thưởng các Lễ trong năm, Tết Âm lịch, Dương lịch. Hoàn Thành công việc trong năm - Lương : 6.000.000- 8.500.000 / tháng, bao 1 cơm, bao đồng phục - Phụ cấp gởi xe - Thưởng lễ, tết','- Mua BHXH,BHYT, BHTN','Cập nhật','https://cdn1.123job.vn/123job/uploads/2023/02/09/2023_02_09______c0fbf3bd49ff848efc812688eaef5af6.png');
INSERT INTO `` (`id`,`Title`,`Company_Name`,`Place`,`Deadline`,`Experience`,`Level`,`Salary`,`Education`,`Description`,`Requirement`,`Number_Employee`,`Source_Picture`) VALUES (16,'Project Manager (English, Waterfall, Scrum, Agile)','NFQ Asia','72 Lê Thánh Tôn, District 1, Ho Chi Minh | 54 Lam Hoanh, Son Tra, Da Nang','10-01-2024','Đang cập nhật','Nhân Viên','Cập nhật','Đang cập nhật','At NFQ, we&039;re all about developing cutting- edge apps, CRMs, ERPs, and other cross- platform products. Both for ourselves and for our clients that include HomeToGo, Kayak, Alaiko, Home24, and many others. We specialize in e- commerce, mobility, and transport & logistics, and we&039;re always eager to tackle new challenges. Whatever the area – from Mobile to UX – we&039;ve got a team that knows it inside out. Join our team of 800+ professionals across Germany, Poland, Lithuania, Vietnam, Thailand, Singapore, and Egypt. Make your own way with us! In this role you will:','Yêu cầu công việc','100 - 200','/images/no_company.png');
INSERT INTO `` (`id`,`Title`,`Company_Name`,`Place`,`Deadline`,`Experience`,`Level`,`Salary`,`Education`,`Description`,`Requirement`,`Number_Employee`,`Source_Picture`) VALUES (17,'Digital Marketing Executive','Công ty TNHH TIM CORP','Tầng 14, Tòa Nhà HM Town, 412 Nguyễn Thị Minh Khai, Phường 5, Quận 3, Thành phố Hồ Chí Minh','05-01-2024','1 năm','Nhân Viên','10 - 14 triệu','Đang cập nhật','Xây dựng nội dung, các bài quảng cáo giới thiệu và phát triển thương hiệu sản phẩm trên các nền tảng xã hội trực tuyến, thương mại điện tử. Cập nhật thông tin insight khách hàng và đề xuất các chương trình marketing phù hợp Lên kế hoạch, thực thi và kiểm soát các chương trình khuyến mãi, chiến dịch digital marketing Làm việc với team thiết kế để tạo ra các hình ảnh quảng cáo cho các nội dung liên quan Phân tích, đánh giá hiệu quả sau chương trình để tăng hiệu suất ROI, KPI,... Quản lý, báo cáo chi phí marketing hàng tháng Cập nhật thông tin thị trường, xu hướng, kiến thức công nghệ để có những đề xuất cải tiến kịp thời.','Quản lý, sắp xếp để đảm bảo công việc theo đúng tiến độ kế hoạch. Thông thạo ứng dụng quảng cáo trên các nền tảng xã hội trực tuyến & thương mại điện tử. Tốt nghiệp Cao đẳng / Đại học trở lên chuyên ngành Marketing hoặc lĩnh vực liên quan. Có kỹ năng content marketing. Có ít nhất 1- 2 năm kinh nghiệm làm việc ở vị trí Digital Marketing hoặc ở vị trí tương đương. Có khả năng tiếng Anh là lợi thế. Kỹ năng làm việc nhóm tốt, chịu được áp lực và tính chất đa dạng của công việc. Kỹ năng phân tích data digital marketing.','50 - 100','https://cdn1.123job.vn/123job/uploads/2023/02/07/2023_02_07______a810e87f54dac680a7453ab7e217d10f.png');
INSERT INTO `` (`id`,`Title`,`Company_Name`,`Place`,`Deadline`,`Experience`,`Level`,`Salary`,`Education`,`Description`,`Requirement`,`Number_Employee`,`Source_Picture`) VALUES (18,'Digital Marketing (Mobile App)','Công Ty Tnhh Puzzle Studio','Tòa nhà Republic Plaza, Số 18E Cộng Hòa','29-02-2024','2 năm','Nhân Viên','600 - 1000 usd','Đang cập nhật','Thực hiện tối ưu hóa landing page nhằm cải thiện tỷ lệ chuyển đổi từ chiến dịch. Lên chiến lược quảng cáo game phù hợp với target audience. Quản lý và tối ưu hiệu quả các chiến dịch quảng cáo game trên các kênh Google, Facebook, Applovin,... Phối hợp với nhóm nội bộ và các đối tác bên ngoài để thực hiện các chiến dịch Phát triển các ý tưởng video quảng cáo sáng tạo, hiệu quả, góp phần tăng CTR và CVR cho chiến dịch. A/B test và tối ưu hóa liên tục để cải thiện hiệu quả quảng cáo, góp phần tăng lượt cài đặt.','tạo những kiến thức cho các thành viên trong team. Kinh nghiệm tối ưu hóa các nội dung quảng cáo trên ít nhất ba nền tảng Google, Facebook, Applovin. Là người chủ động, cam kết, nhận trách nhiệm. Tuân thủ các quy tắc, văn hóa công ty . Có khả năng học hỏi và tiến bộ không ngừng. Có góc nhìn đa chiều và thích ứng nhanh với môi trường phát triển nhanh về công nghệ. Kinh nghiệm quản lý ngân sách quảng cáo quy mô lớn Kỹ năng làm việc độc lập và làm việc nhóm hiệu quả cùng tư duy phản biện. Kỷ luật và muốn chia sẻ, kiến Từ 2 năm kinh nghiệm cho các ứng dụng di động, đặc biệt ngành game là một lợi thế','10 - 25','https://cdn1.123job.vn/123job/uploads/2023/02/07/2023_02_07______fbd48c204f65755b2c4a5ac552950198.jpg');
INSERT INTO `` (`id`,`Title`,`Company_Name`,`Place`,`Deadline`,`Experience`,`Level`,`Salary`,`Education`,`Description`,`Requirement`,`Number_Employee`,`Source_Picture`) VALUES (19,'Nhân Viên Phục Vụ - Waiter / Waitress','CÔNG TY TNHH GODMOTHER HÀ NỘI','Cập nhật','04-01-2024','Dưới 1 năm','Nhân Viên','7 - 9 triệu','Đang cập nhật','Thực hiện các công việc đầu ca','Có 6 tháng- 1 năm kinh nghiệm trở lên vị trí Phục vụ Trẻ và năng động. Tính cách: Vui vẻ, nhiệt tình, có trách nhiệm trong công việc, có định hướng làm việc lâu dài với nhà hàng. Có thể giao tiếp tiếng anh Ưu tiên ứng viên đã từng làm việc trong môi trường nhà hàng, khách sạn.','Cập nhật','/images/no_company.png');
